// input 상태관리 
import React, { useState } from 'react'; 

function InputSample(){
  const [text , setText] = useState(''); 

  const onChange = (e) =>{
    setText(e.target.value);
  };

  const onReset = () =>{
    setText('');
  };

  return(
    <div>
      <input onClick={onChange} value={text}/>
      <button onClick={onReset}>초기화</button>
      <div>
        <b>값: {text} </b>
      </div>
    </div>
  );
}

export default InputSample;
