// 9. 여러개의 input 상태관리
import React, { useState } from 'react';

function InputSample2() {
  const [inputs, setInputs] = {
    name: '',
    nickname: '',
  };

  const onChange = (e) => {
    const { value, name } = e.target;

    setInputs({
      ...inputs,
      [name]: value,
    });
  };

  const onReset = () => {
    setInputs({
      name: '',
      nickname: '',
    });
  };

  return (
    <>
      <input name="name" />
      <input />
      <button>초기화</button>
      <div>
        <b>값: </b>
        {name} ({nickname})
      </div>
    </>
  );
}

export default InputSample2;
